mapper.update:
update festival
place_like = #{place_like}, hit = #{hit}, wish_list_fes = #{wish_list_fes},
where festival_id = #{festival_id}

mapper.insert:

    select seq_review.nextval from dual

insert into festival_likes (
	like_id,
    member_id,
    festival_id
) values (
    #{like_id},
    #{member_id},
    #{festival_id}
)


    select seq_review.nextval from dual

insert into wish_list_fes (
	wishboard_id,
    member_id,
    festival_id
) values (
    #{wishboard_id},
    #{member_id},
    #{festival_id}
)

mapper.select:
SELECT CASE
WHEN EXISTS (
SELECT 1 FROM festival_likes
WHERE festival_id = #{festival_id}
AND member_id = #{member_id}
)
THEN TRUE
ELSE FALSE
END AS isFavorite
