mapper.select:
SELECT CASE
WHEN EXISTS (
SELECT 1 FROM festival_likes
WHERE festival_id = #{festival_id}
AND member_id = #{member_id}
)
THEN TRUE
ELSE FALSE
END AS isFavorite

SELECT CASE
WHEN EXISTS (
SELECT 1 FROM wish_list_fes
WHERE festival_id = #{festival_id}
AND member_id = #{member_id}
)
THEN TRUE
ELSE FALSE
END AS isWishList

SELECT like_id,member_id,festival_id
FROM festival_likes
WHERE festival_id = #{festival_id}
AND member_id = #{member_id}

SELECT *
FROM wish_list_fes
WHERE festival_id = #{festival_id}
AND member_id = #{member_id}

mapper.update:
update festival
place_like = #{place_like}, hit = #{hit}, wish_list_fes = #{wish_list_fes},
where festival_id = #{festival_id}

mapper.insert:
insert into festival_likes (
member_id,
festival_id
) values (
#{member_id},
#{festival_id}
)

insert into wish_list_fes (
member_id,
festival_id
) values (
#{member_id},
#{festival_id}
)
