mapper.select:
SELECT CASE
WHEN EXISTS (
SELECT 1 FROM tourist_spot_likes
WHERE tourist_Spot_id = #{tourist_Spot_id}
AND member_id = #{member_id}
)
THEN TRUE
ELSE FALSE
END AS isFavorite

SELECT CASE
WHEN EXISTS (
SELECT 1 FROM wish_list
WHERE tourist_Spot_id = #{tourist_Spot_id}
AND member_id = #{member_id}
)
THEN TRUE
ELSE FALSE
END AS isWishList

mapper.insert:
insert into tourist_spot_likes (
member_id,
tourist_Spot_id
) values (
#{member_id},
#{tourist_Spot_id}
)

mapper.select:
SELECT like_id,member_id,tourist_spot_id
FROM tourist_spot_likes
WHERE tourist_spot_id = #{tourist_spot_id}
AND member_id = #{member_id}

mapper.insert:
insert into wish_list (
member_id,
tourist_Spot_id
) values (
#{member_id},
#{tourist_Spot_id}
)
